<template>
    <div class="login-wrap" id="particles">
        <div class="ms-title"></div>
        <div class="ms-login">
            <el-form :model="ruleForm" :rules="rules" ref="ruleForm" label-width="0px" class="demo-ruleForm">
                <el-form-item prop="username">
                    <el-input v-model="ruleForm.username" placeholder="用户名"></el-input>
                </el-form-item>
                <el-form-item prop="password">
                    <el-input type="password" placeholder="登录密码" v-model="ruleForm.password" @keyup.enter.native="submitForm('ruleForm')"></el-input>
                </el-form-item>
                <el-checkbox v-model="ruleForm.keepPwd">记住密码</el-checkbox>
                <div class="login-btn">
                    <el-button type="primary" @click="linkToPath('infobasic')" :disabled="loginable"><i class="el-icon-loading" v-show="loginLoading"></i>登录</el-button>
                </div>
                <span style="font-size:12px;color:#999;">Tips : 请输入用户名和密码。</span>
            </el-form>
        </div>
    </div>
</template>

<script>
import md5 from 'md5'
import qs from 'qs'
import instance from '../common/js/axios'
import $ from 'jquery'
global.$ = global.jQuery = $
require('../common/plugin/jquery.particleground.js')
export default {
    data () {
        return {
            loginLoading: false,
            loginable: false,
            ruleForm: {
                username: '',
                password: '',
                keepPwd: false
            },
            rules: {
                username: [
                    { required: true, message: '请输入用户名', trigger: 'blur' }
                ],
                password: [
                    { required: true, message: '请输入密码', trigger: 'blur' }
                ]
            }
        }
    },
    methods: {
        submitForm(formName) {
            const self = this
            self.$refs[formName].validate((valid) => {
            if (valid) {
                let prefix = 'http://localhost:8877' + 'login'
                let login_param = {
                  name: '',
                  password: ''
                }
                login_param.name = self.ruleForm.username
                login_param.password = self.ruleForm.password
            } else {
                console.log('error submit!!')
                return false
            }
          })
        },
        linkToPath (path, query) {
          let self = this
          this.loginLoading = true
          this.loginable = true
          self.$refs['ruleForm'].validate((valid) => {
            if (valid) {
                let login_params = qs.stringify({
                  name: this.ruleForm.username,
                  password: md5(this.ruleForm.password)
                })
                instance.post('login', login_params).then(function (response) {
                  response.data = JSON.parse(response.data)
                  if(response.data.status){
                    if(self.ruleForm.keepPwd) {
                      window.localStorage.setItem('keppBtn', true)
                      window.localStorage.setItem('pms_name', self.ruleForm.username)
                      window.localStorage.setItem('pms_password', self.ruleForm.password)
                    } else{
                      window.localStorage.removeItem('pms_name')
                      window.localStorage.removeItem('pms_password')
                    }
                    window.sessionStorage.setItem('userToken', response.data.resultObj.id)
                    window.sessionStorage.setItem('token', response.data.resultObj.token)

                    let res_string = JSON.stringify(response.data.resultObj)
                    window.sessionStorage.setItem('res_string', res_string)
                    self.$message({
                      showClose: true,
                      message: '登录成功,欢迎回来~',
                      type: 'success'
                    })
                    self.$router.push({
                      path: path
                    })
                  } else {
                    self.$message({
                      showClose: true,
                      message: response.data.message,
                      type: 'warning'
                    })
                  }
                  self.loginLoading = false
                  self.loginable = false
                }).catch(function (error) {
                  let errMess = error.message ? error.message : '网络错误!'
                  self.$message({
                    showClose: true,
                    message: errMess,
                    type: 'warning'
                  })
                  self.loginLoading = false
                  self.loginable = false
                })
            } else {
              self.$message({
                showClose: true,
                message: '网络错误!',
                type: 'warning'
              })
              self.loginLoading = false
              self.loginable = false
              return false
            }
          })
        }
    },
    mounted () {
      if (window.localStorage.getItem('keppBtn')) {
        this.ruleForm.keepPwd = true
        this.ruleForm.username = window.localStorage.getItem('pms_name')
        this.ruleForm.password = window.localStorage.getItem('pms_password')
      }
      $(document).ready(function () {
        $('#particles').particleground({
          dotColor: '#a2a2a2',
          lineColor: '#d3d3d3'
        });
        $('.intro').css({
          'margin-top': -($('.intro').height() / 2)
        });
      })
      //http://localhost:8878/taskCollectionCenterWeb/
      window.sessionStorage.setItem('baseUrl', '')
    }
}
</script>

